mapper.select:
SELECT b.popup_no as popupNo, b.booking_time as bookingTime, b.booking_dt as bookingDt
FROM booking b
JOIN popup p ON b.popup_no = p.popup_no
WHERE p.popup_no = #{popupNo} 
AND p.popup_start_dt = #{popupStartDt}
AND p.popup_end_dt = #{popupEndDt}

SELECT booking_no as bookingNo, member_no as memberNo, popup_no as popupNo, booking_time as bookingTime, booking_dt as bookingDt 
FROM booking
WHERE popup_no = #{popupNo}
AND booking_dt = #{bookingDt}
AND booking_time = #{bookingTime}
FOR UPDATE
